echo "/* Skip Link */
.skip-link {
    position: absolute;
    top: -40px;
    left: 0;
    background: #000;
    color: white;
    padding: 8px;
    z-index: 100;
    transition: top 0.3s;
}

.skip-link:focus {
    top: 0;
}
" >> css/styles.css

echo "
/* Experience and References */
.experience-item, .reference-item {
    display: flex;
    align-items: flex-start;
    gap: 20px;
    margin-bottom: 20px;
    padding-bottom: 20px;
    border-bottom: 1px solid #eee;
}

.experience-item:last-child, .reference-item:last-child {
    border-bottom: none;
}

.experience-logo, .reference-photo {
    width: 80px;
    height: 80px;
    object-fit: cover;
    border-radius: 8px;
    flex-shrink: 0;
    background-color: #f0f0f0; /* Placeholder background */
}

.reference-photo {
    border-radius: 50%; /* Circular photos for people */
}

.experience-content, .reference-content {
    flex: 1;
}

@media (max-width: 600px) {
    .experience-item, .reference-item {
        flex-direction: column;
        align-items: center;
        text-align: center;
    }

    .experience-logo, .reference-photo {
        margin-bottom: 10px;
    }
}
" >> css/styles.css

echo "
/* Search */
#search-form {
    display: inline-block;
    margin-left: 1.25rem;
}

#search-input {
    padding: 0.3rem;
    border-radius: 4px;
    border: 1px solid #ccc;
}

#search-results {
    background: #fff;
    padding: 1.25rem;
    margin: 1.25rem auto;
    max-width: 50rem;
    border-radius: 8px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

#search-results:empty {
    display: none;
}

.visually-hidden {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}
" >> css/styles.css

echo "
/* Contact Form */
.contact-form {
    max-width: 40rem;
    margin: 0 auto;
}

.form-group {
    margin-bottom: 1.25rem;
}

.form-group label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: bold;
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: 0.6rem;
    border: 1px solid #ccc;
    border-radius: 4px;
    font-family: inherit;
    box-sizing: border-box; /* Ensure padding doesn't affect width */
}

.btn-submit {
    display: inline-block;
    background: #333;
    color: #fff;
    padding: 0.6rem 1.25rem;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    font-size: 1rem;
    transition: background 0.3s;
}

.btn-submit:hover {
    background: #555;
}
" >> css/styles.css

echo "
/* Form Feedback and Validation */
.form-message {
    padding: 1rem;
    margin-bottom: 1.25rem;
    border-radius: 4px;
    display: none; /* Hidden by default */
}

.form-message.success {
    background-color: #d4edda;
    color: #155724;
    border: 1px solid #c3e6cb;
    display: block;
}

.form-message.error {
    background-color: #f8d7da;
    color: #721c24;
    border: 1px solid #f5c6cb;
    display: block;
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #333;
    box-shadow: 0 0 5px rgba(51, 51, 51, 0.2);
}

.form-group input:invalid:not(:placeholder-shown),
.form-group textarea:invalid:not(:placeholder-shown) {
    border-color: #dc3545;
}

.form-group input:valid:not(:placeholder-shown),
.form-group textarea:valid:not(:placeholder-shown) {
    border-color: #28a745;
}

.form-help {
    display: block;
    font-size: 0.85rem;
    color: #666;
    margin-top: 0.3rem;
}
" >> css/styles.css

echo "
/* Scroll to Top Button */
.scroll-to-top {
    position: fixed;
    bottom: 2rem;
    right: 2rem;
    background-color: #333;
    color: #fff;
    border: none;
    border-radius: 50%;
    width: 3rem;
    height: 3rem;
    font-size: 1.5rem;
    cursor: pointer;
    box-shadow: 0 2px 5px rgba(0,0,0,0.3);
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.3s, visibility 0.3s, background-color 0.3s;
    z-index: 1000;
    display: flex;
    align-items: center;
    justify-content: center;
}

.scroll-to-top.visible {
    opacity: 1;
    visibility: visible;
}

.scroll-to-top:hover {
    background-color: #555;
}

.scroll-to-top:focus {
    outline: 2px solid #000;
    outline-offset: 2px;
}
" >> css/styles.css

echo "
/* Header Controls (Search + Language) */
.header-controls {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.language-selector select {
    padding: 0.3rem;
    border-radius: 4px;
    border: 1px solid #ccc;
    background-color: #fff;
    color: #333;
    font-family: inherit;
    cursor: pointer;
}

@media (max-width: 48rem) {
    .header-controls {
        flex-direction: column;
        width: 100%;
    }

    .language-selector {
        width: 100%;
        display: flex;
        justify-content: center;
    }
}
" >> css/styles.css
